import React,{useState} from 'react';
import {Formik} from 'formik';
import * as Yup from 'yup';

import {Card,Row,Col,Dropdown} from 'react-bootstrap'

const Signup = () => {

    const [month,setMonth]= useState('Sep')
    const [date,setDate]= useState(25)
    const [year,setYear]= useState(2020)

    let dates = [];
    for(var i =1 ; i <=31;i++ ){
        dates.push(i)
    }

    let years = []
    for(i =2020;i>=1905;i--){
        years.push(i) 
    }

    return (
        <div>
            <Card className="card-container">
            <Card.Body>
            <Card.Title>Sign Up</Card.Title>
            <Card.Subtitle className="mb-2 text-muted">It is quick and easy</Card.Subtitle>
            <hr/>
            <Formik
                initialValues = {{username:'',password:''}}
                onSubmit={(values,{setSubmitting})=>{
                    setSubmitting(false);
                    console.log(values)

                    // props.login(values)                

                }}
                validationSchema={Yup.object().shape({
                    firstName : Yup.string().required("Required").max(20,"Maximum 20 characters"),
                    lastName : Yup.string().required("Required").max(20,"Maximum 20 characters"),
                    username: Yup.string().required("Required"),
                    password: Yup.string().required("No password entered").required("Required").matches(/[a-zA-Z0-9]{8,}/,"Enter valid password")
                })}
                >

                {formik => {
                    const {values,touched, errors, handleChange,handleBlur, handleSubmit} = formik;

                    return (
                        <form onSubmit={handleSubmit}>
                            <Row className="form-group">
                            <Col>
                            <input 
                                name = "firstName"
                                type="text"
                                placeholder="First Name"
                                value = {values.firstName}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                className= {"form-control text-box " + (errors.firstName && touched.firstName ? "errorField":"")}
                            />

                            {errors.firstName && touched.firstName && (
                                <div className="error">{errors.firstName}</div>
                            )}
                            </Col>
                            <Col>
                            <input
                                name="lastName"
                                type="text"
                                placeholder="Last Name"
                                value={values.lastName}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                className={"form-control text-box "+ (errors.lastName && touched.lastName ? "errorField":"")}
                            />
                            {errors.lastName && touched.lastName && (
                                <div className="error">{errors.lastName}</div>
                            )}
                            </Col>
                            </Row>
                            {/* Username */}
                            <Row className="form-group">
                            <input 
                                name = "username"
                                type="text"
                                placeholder="Email or Phone Number"
                                value = {values.username}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                className= {"form-control text-box " + (errors.username && touched.username ? "errorField":"")}
                            />

                            {errors.username && touched.username && (
                                <div className="error">{errors.username}</div>
                            )}
                            </Row>
                            {/* Password */}
                            <Row className="form-group">
                            <input
                                name="password"
                                type="password"
                                placeholder="New Password"
                                value={values.password}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                className={"form-control text-box "+ (errors.password && touched.password ? "errorField":"")}
                            />
                            {errors.password && touched.password && (
                                <div className="error">{errors.password}</div>
                            )}
                            </Row>
                            <Row>
                            <label htmlFor="birthDay" className="form-label">Birthday</label>
                            </Row>
                            <Row>
                            <Col>
                                    <Dropdown>
                                    <Dropdown.Toggle variant="transparent" id="dropdown-basic">
                                        {month}
                                    </Dropdown.Toggle>

                                    <Dropdown.Menu>
                                        {['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov',"Dec"].map(
                                            (m)=> <Dropdown.Item onClick={()=>setMonth(m)}>{m}</Dropdown.Item>
                                        )}
                                    </Dropdown.Menu>
                                    </Dropdown>
                                </Col>
                                <Col>
                                <Dropdown>
                                    <Dropdown.Toggle variant="transparent" id="dropdown-basic">
                                        {date}
                                    </Dropdown.Toggle>

                                    <Dropdown.Menu>
                                        {dates.map(
                                            (d)=> <Dropdown.Item onClick={()=>setDate(d)}>{d}</Dropdown.Item>
                                        )}
                                    </Dropdown.Menu>
                                    </Dropdown>
                                </Col>
                                <Col>
                                    <Dropdown>
                                    <Dropdown.Toggle variant="transparent" id="dropdown-basic">
                                        {year}
                                    </Dropdown.Toggle>

                                    <Dropdown.Menu>
                                        {years.map(
                                            (y)=> <Dropdown.Item onClick={()=>setYear(y)}>{y}</Dropdown.Item>
                                        )}
                                    </Dropdown.Menu>
                                    </Dropdown>
                                </Col>

                            </Row>
                            <Row>
                            <label htmlFor="gender" className="form-label">Gender</label>
                            </Row>
                            <Row>

                            </Row>
                            {/* Login button */}
                            <Row className="form-group jc">
                            <button type="submit" disabled={!(formik.isValid && formik.dirty) } className="btn btn-success btn-lg">
                                Signup
                            </button>
                            </Row>
                        </form>
                    );

                }}

            </Formik>
            </Card.Body>
            </Card>
        </div>
    )
}

export default Signup;
