const initState = {
    user:null,
    showLoginForm:true,
    loading:true
}

export const reducer = (state=initState,action)=>{
    switch(action.type){
        case "SHOW_SIGNUP":
            return {
                ...state,
                showLoginForm:false
            }
    }

    return state
}
